import React from 'react';
import { CircularProgress, Typography, Box, LinearProgress, keyframes } from '@mui/material';
import { styled } from '@mui/material/styles';
import { Modality } from '../../types';

interface LoadingIndicatorProps {
  modality?: Modality;
  progress?: number;
  message?: string;
  tips?: string[];
}

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const AnimatedBox = styled(Box)`
  animation: ${fadeIn} 0.5s ease-in-out;
`;

const LoadingContainer = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  width: '100%',
  height: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(3),
  backgroundColor: 'rgba(255, 255, 255, 0.8)',
  borderRadius: theme.shape.borderRadius * 2,
  backdropFilter: 'blur(8px)',
}));

const ModalityIcon = styled('div')(({ theme }) => ({
  fontSize: '3rem',
  marginBottom: theme.spacing(2),
}));

const ProgressContainer = styled(Box)(({ theme }) => ({
  width: '80%',
  maxWidth: '300px',
  marginTop: theme.spacing(2),
}));

export const LoadingIndicator: React.FC<LoadingIndicatorProps> = ({
  modality,
  progress,
  message = 'Processing...',
  tips = [
    'This might take a minute...',
    'AI models are working their magic...',
    'Almost there...',
    'Creating something amazing...',
  ],
}) => {
  const [currentTip, setCurrentTip] = React.useState(0);

  React.useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTip((prev) => (prev + 1) % tips.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [tips.length]);

  const getModalityIcon = () => {
    switch (modality) {
      case Modality.Image:
        return 'üñºÔ∏è';
      case Modality.Video:
        return 'üé•';
      case Modality.Audio:
        return 'üéµ';
      case Modality.Text:
        return 'üìù';
      case Modality.ThreeDimentional:
        return 'üéÆ';
      case Modality.Sketch:
        return '‚úèÔ∏è';
      default:
        return '‚ö°';
    }
  };

  return (
    <LoadingContainer>
      <ModalityIcon>{getModalityIcon()}</ModalityIcon>

      <CircularProgress
        size={60}
        thickness={4}
        sx={{
          color: 'secondary.main',
        }}
      />

      {progress !== undefined && (
        <ProgressContainer>
          <LinearProgress
            variant="determinate"
            value={progress}
            sx={{
              height: 8,
              borderRadius: 4,
              backgroundColor: 'rgba(0, 0, 0, 0.1)',
              '& .MuiLinearProgress-bar': {
                borderRadius: 4,
              },
            }}
          />
          <Typography
            variant="caption"
            sx={{
              display: 'block',
              textAlign: 'center',
              marginTop: 1,
            }}
            color="textSecondary"
          >
            {Math.round(progress)}%
          </Typography>
        </ProgressContainer>
      )}

      <Typography
        variant="h6"
        sx={{
          textAlign: 'center',
          fontWeight: 500,
        }}
        color="textPrimary"
      >
        {message}
      </Typography>

      <AnimatedBox
        sx={{
          opacity: 0.8,
        }}
      >
        <Typography
          variant="body2"
          sx={{
            textAlign: 'center',
            color: 'text.secondary',
          }}
        >
          {tips[currentTip]}
        </Typography>
      </AnimatedBox>
    </LoadingContainer>
  );
};
