import React from 'react';
import { CircularProgress, Typography, Box, LinearProgress } from '@mui/material';
import { Modality } from '../../types';

interface LoadingIndicatorProps {
  modality?: Modality;
  progress?: number;
  message?: string;
  tips?: string[];
}

export const LoadingIndicator: React.FC<LoadingIndicatorProps> = ({
  modality,
  progress,
  message = 'Processing...',
  tips = [
    'This might take a minute...',
    'AI models are working their magic...',
    'Almost there...',
    'Creating something amazing...',
  ],
}) => {
  const [currentTip, setCurrentTip] = React.useState(0);

  React.useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTip((prev) => (prev + 1) % tips.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [tips.length]);

  const getModalityIcon = () => {
    switch (modality) {
      case Modality.Image:
        return 'üñºÔ∏è';
      case Modality.Video:
        return 'üé•';
      case Modality.Audio:
        return 'üéµ';
      case Modality.Text:
        return 'üìù';
      default:
        return '‚ö°';
    }
  };

  return (
    <Box
      className="flex flex-col items-center justify-center w-full h-full p-8 space-y-6 animate-pulse"
      sx={{
        backgroundColor: 'rgba(255, 255, 255, 0.8)',
        borderRadius: '16px',
      }}
    >
      <div className="text-4xl mb-4">{getModalityIcon()}</div>

      <CircularProgress
        size={60}
        thickness={4}
        sx={{
          color: 'secondary.main',
        }}
      />

      {progress !== undefined && (
        <Box sx={{ width: '80%', maxWidth: '300px' }}>
          <LinearProgress
            variant="determinate"
            value={progress}
            sx={{
              height: 8,
              borderRadius: 4,
              backgroundColor: 'rgba(0, 0, 0, 0.1)',
              '& .MuiLinearProgress-bar': {
                borderRadius: 4,
              },
            }}
          />
          <Typography
            variant="caption"
            className="mt-2 text-center block"
            color="textSecondary"
          >
            {Math.round(progress)}%
          </Typography>
        </Box>
      )}

      <Typography
        variant="h6"
        className="text-center font-medium"
        color="textPrimary"
      >
        {message}
      </Typography>

      <Typography
        variant="body2"
        className="text-center text-gray-500 animate-fade-in"
        sx={{
          opacity: 0.8,
          animation: 'fadeIn 0.5s ease-in-out',
        }}
      >
        {tips[currentTip]}
      </Typography>
    </Box>
  );
};

// Add this to your global CSS or tailwind.config.js
const styles = `
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-fade-in {
  animation: fadeIn 0.5s ease-in-out;
}
`;

// Create a style element and append it to the document head
if (typeof document !== 'undefined') {
  const styleElement = document.createElement('style');
  styleElement.innerHTML = styles;
  document.head.appendChild(styleElement);
}
